import React, {useEffect, useState }  from 'react';
import {LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend} from 'recharts';
import Axios from 'axios';

const temp =[{
    name: String(),
    Nhiễm: Number(),
    Tử_vong: Number(),
    Khỏi: Number()
}]

function ChartGlobal () {
    const [Patients, setPatients] = useState({});
    const [items,setItems] = useState([{
        name: String(),
        Nhiễm: Number(),
        Tử_vong: Number(),
        Khỏi: Number()
    }])

    useEffect(() => {
        Axios.get("https://td.fpt.ai/corona/corona-total.json")
            .then(
                (result) => {
                    setPatients(result.data);
                    setItems(temp);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    // setIsLoaded(true);
                    // setError(error);
                }
            )
    }, [])

    const arrayOfObj = [].concat( Object.values(Patients));
    const arrayKeys = [].concat( Object.keys(Patients));
    
    if(temp[0].name === ""){
    for (let i=0; i<arrayKeys.length; i++){
        temp[i] = {
            name : arrayKeys[i],
            Nhiễm : [].concat(arrayOfObj[i])[0],
            Tử_vong : [].concat(arrayOfObj[i])[1],
            Khỏi : [].concat(arrayOfObj[i])[2]
        }
    }}

    return <LineChart
            width={800}
            height={700}
            data={items}
            margin={{
                top: 5, right: 10, left: 50, bottom: 5,
            }}
        >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="Nhiễm" stroke="#FF0000" activeDot={{ r: 8 }} />
            <Line type="monotone" dataKey="Tử_vong" stroke="#000000" />
            <Line type="monotone" dataKey="Khỏi" stroke="#0033FF" />
        </LineChart>

}

export default ChartGlobal;
