import React, {useEffect, useState }  from 'react';
import {LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend} from 'recharts';
import Axios from 'axios';

const data = [
    {
        name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
    },
    {
        name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
    },
    {
        name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
    },
    {
        name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
    },
    {
        name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
    },
    {
        name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
    },
    {
        name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
    },
];

const items = [{
    name: String(),
    nhiem: Number(),
    nghiNhiem: Number(),
    hoiPhuc: Number()
}];

function ChartVN () {
    const [Patients, setPatients] = useState({});

    useEffect(() => {
        Axios.get("https://td.fpt.ai/corona/corona-chart-vn.json")
            .then(
                (result) => {
                    setPatients(result.data);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    // setIsLoaded(true);
                    // setError(error);
                }
            )
    }, [])

    const arrayOfObj = [].concat( Object.values(Patients));
    const arrayKeys = [].concat( Object.keys(Patients));
    
    if(items[0].name === ""){
    for (let i=0; i<arrayKeys.length; i++){
        items[i] = {
            name : arrayKeys[i],
            nhiem : [].concat(arrayOfObj[i])[0],
            nghiNhiem : [].concat(arrayOfObj[i])[1],
            hoiPhuc : [].concat(arrayOfObj[i])[2]
        }
    }}

        return (
            
        <LineChart
            width={600}
            height={500}
            data={items}
            margin={{
                top: 5, right: 30, left: 5, bottom: 5,
            }}
        >
            {console.log(data)}
            {console.log(items)}
            
            <Line type="monotone" dataKey="nhiem" stroke="#8884d8" activeDot={{ r: 8 }} />

        </LineChart>
    );
}

export default ChartVN;
