import React from 'react';
import './App.css';
import CovidDashboard from "./components/CovidDashboard";
import Stats from "./components/stats"
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import {Switch, Route, Link } from "react-router-dom";

function App() {
    return ( <>
        <Row>
            <Col><h2 id="linkroute"><Link to="/map">Bản đồ việt nam</Link></h2></Col>
            <Col><h2 id="linkroute"><Link to="/stats">Đồ thị số ca mắc</Link></h2></Col>
        </Row>
        <hr />

        <Switch>
            <Route exact path="/">
                <CovidDashboard / >
            </Route>
            <Route path="/map">
                <CovidDashboard / >
            </Route>
            <Route path="/stats">
                <Stats / >
            </Route>
        </Switch>
    </>
    );   
}

export default App;
