import React from 'react';
import './App.css';
import CovidDashboard from "./components/CovidDashboard";
import Stats from "./components/stats"
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import {Switch, Route, Link } from "react-router-dom";

function App() {
    return ( <>
        <Row>
            <Col><Link style={{ textDecoration: 'none', color:'black'}} to="/map"><h2 id="linkroute">Bản đồ việt nam</h2></Link></Col>
            <Col><Link style={{ textDecoration: 'none', color:'black'}} to="/stats"><h2 id="linkroute">Đồ thị số ca mắc</h2></Link></Col>
        </Row>

        <Switch>
            <Route exact path="/">
                <CovidDashboard / >
            </Route>
            <Route path="/map">
                <CovidDashboard / >
            </Route>
            <Route path="/stats">
                <Stats / >
            </Route>
        </Switch>
    </>
    );   
}

export default App;
