import React, {useEffect, useState } from "react";
import { Container } from "@material-ui/core";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import ChartVN from "./ChartVN"
import Axios from 'axios';

function stats () {
    const [itemss] = useState([{
        name: String(),
        uv: Number(),
        pv: Number(),
        amt: Number()
    }])
    const [Patients, setPatients] = useState({});

    useEffect(() => {
        Axios.get("https://td.fpt.ai/corona/corona-chart-vn.json")
            .then(
                (result) => {
                    setPatients(result.data);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    // setIsLoaded(true);
                    // setError(error);
                }
            )
    }, [])

    const arrayOfObj = [].concat( Object.values(Patients));
    const arrayKeys = [].concat( Object.keys(Patients));
    
    if(items[0].name === ""){
    for (let i=0; i<arrayKeys.length; i++){
        items[i] = {
            name : arrayKeys[i],
            uv : [].concat(arrayOfObj[i])[0],
            pv : [].concat(arrayOfObj[i])[1],
            amt : [].concat(arrayOfObj[i])[2]
        }
    }}


    return <Container> 
        <Row>
            <Col><h2>Việt Nam</h2>
                {itemss &&< ChartVN items={itemss}/>}
            </Col>
            <Col><h2>Thế giới</h2></Col>
        </Row>
    </Container>
}

export default stats;
