import React, {useEffect, useState }  from 'react';
import {LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend} from 'recharts';
import Axios from 'axios';

const data = [
    {
        name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
    },
    {
        name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
    },
    {
        name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
    },
    {
        name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
    },
    {
        name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
    },
    {
        name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
    },
    {
        name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
    },
];
const temp =[{
    name: String(),
    Nhiem: Number(),
    NghiNhiem: Number(),
    HoiPhuc: Number()
}]

function ChartVN () {
    const [Patients, setPatients] = useState({});
    const [items,setItems] = useState([{
        name: String(),
        Nhiem: Number(),
        NghiNhiem: Number(),
        HoiPhuc: Number()
    }])

    useEffect(() => {
        Axios.get("https://td.fpt.ai/corona/corona-chart-vn.json")
            .then(
                (result) => {
                    setPatients(result.data);
                    setItems(temp);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    // setIsLoaded(true);
                    // setError(error);
                }
            )
    }, [])

    const arrayOfObj = [].concat( Object.values(Patients));
    const arrayKeys = [].concat( Object.keys(Patients));
    
    if(temp[0].name === ""){
    for (let i=0; i<arrayKeys.length; i++){
        temp[i] = {
            name : arrayKeys[i],
            uv : [].concat(arrayOfObj[i])[0],
            pv : [].concat(arrayOfObj[i])[1],
            amt : [].concat(arrayOfObj[i])[2]
        }
    }}

    return <LineChart
            width={600}
            height={500}
            data={items}
            margin={{
                top: 5, right: 30, left: 5, bottom: 5,
            }}
        >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="Nhiem" stroke="#8884d8" activeDot={{ r: 8 }} />
            <Line type="monotone" dataKey="NghiNhiem" stroke="#82ca9d" />
            <Line type="monotone" dataKey="HoiPhuc" stroke="#00FF00" />
        </LineChart>

}

export default ChartVN;
